# app.py
# Ein einfaches Python-Backend, das die App-Daten √ºber eine API bereitstellt.
# Dies erm√∂glicht eine saubere Trennung von Frontend (HTML/CSS/JS) und Backend (Python).

import json
from flask import Flask, jsonify, send_from_directory

app = Flask(__name__)

# Lade die App-Daten aus der JSON-Datei
# Stellen Sie sicher, dass eine Datei namens 'app_data.json' im selben Ordner existiert.
try:
    with open('app_data.json', 'r', encoding='utf-8') as f:
        app_data = json.load(f)
except FileNotFoundError:
    app_data = {"error": "app_data.json not found."}

@app.route('/')
def serve_index():
    """Stellt die Haupt-HTML-Datei der App bereit."""
    return send_from_directory('.', 'index.html')

@app.route('/<path:path>')
def serve_static(path):
    """Stellt statische Dateien wie CSS und JS bereit."""
    return send_from_directory('.', path)

@app.route('/api/data')
def get_app_data():
    """API-Endpunkt, der die App-Daten als JSON zur√ºckgibt."""
    return jsonify(app_data)

if __name__ == '__main__':
    # Startet den Server im Debug-Modus f√ºr die Entwicklung
    app.run(debug=True)
